import React from 'react';
const CustomAlert = (color, title, msg) => {
  return (
    <div className={`alert-${color}`} id={color}>
        <h2 className='alert-title'>{title}</h2>
        <p className='alert-description'>{msg}</p>
      
    </div>
  );
};
export default CustomAlert;
